import UIKit
import Alamofire

class SignupWorker
{
    func createAccount(request: Signup.Request, completion: @escaping (_ response: Signup.Response) -> ())
    {
        let url: String = AppConstants.ENDPOINT+"/users/signup"
        
        let params: Parameters = [
            "name": request.name,
            "email": request.email,
            "password": request.password
        ]
        
        Alamofire
            .request(url, method: .post, parameters: params)
            .validate()
            .responseJSON { response in
                switch (response.result) {
                case .success(let data):
                    let data = data as! [String: Any?]
                    let res = Signup.Response(successful: true, data: data)
                    completion(res)
                case .failure(_):
                    let res = Signup.Response(successful: false)
                    completion(res)
                }
            }
    }
}
